    var containerName = "#viz";
var layoutRoot;

function visit(parent, visitFn, childrenFn) {
    if (!parent) return;

    visitFn(parent);

    var children = childrenFn(parent);
    if (children) {
        var count = children.length;
        for (var i = 0; i < count; i++) {
            visit(children[i], visitFn, childrenFn);
        }
    }
}

function drawTree(treeData) {
    var container = $(containerName),
        totalNodes = 0,
        maxLabelLength = 0;

    var options = {nodeRadius: 7, fontSize: 12};

    visit(treeData, function(d) {
        totalNodes++;
        maxLabelLength = Math.max(d.name.length, maxLabelLength);
    }, function(d) {
        return d.contents && d.contents.length > 0 ? d.contents : null;
    });
    

    // size of the diagram
    var size = { width:$(containerName).outerWidth(), 
        height: totalNodes * 15};

    var tree = d3.layout.tree()
        .sort(null)
        .size([size.height, size.width - maxLabelLength*options.fontSize])
        .children(function(d) {
            return (!d.contents || d.contents.length == 0) ? 
                null : d.contents;
    });

    var nodes = tree.nodes(treeData);
    var links = tree.links(nodes);

    layoutRoot = d3.select(containerName)
        .append("svg:svg")
            .attr("width", size.width)
            .attr("height", size.height + 20)
        .append("svg:g")
            .attr("class", "container")
            .attr("transform", "translate(" + maxLabelLength + ",0)");

    var link = d3.svg.diagonal()
        .projection(function(d) {
            return [d.y, d.x];
        });

    layoutRoot.selectAll("path.link")
            .data(links)
        .enter()
            .append("svg:path")
            .style("opacity", 0)
            .attr("class", "link")
            .transition()
                .duration(600)
                .attr("d", link)
                .style("opacity", 1);

    var nodeGroup = layoutRoot.selectAll("g.node")
            .data(nodes)
        .enter()
            .append("svg:g")
            .attr("class", "node")
            .attr("transform", function(d) {
                return "translate(" + d.y + "," + d.x + ")";
            });

    nodeGroup
        .append("svg:circle")
        .attr("class", "node-dot")
        .on("mouseover", shrink)
        .on("mouseout", grow)
            .transition()
            .duration(500)
            .attr("r", options.nodeRadius);

    function shrink() {
        d3.select(this).transition()
            .duration(500)
            .attr("r", options.nodeRadius - 2);
    }

    function grow() {
        d3.select(this).transition()
            .duration(1000)
            .attr("r", options.nodeRadius + 2)
            .transition()
            .delay(800)
            .attr("r", options.nodeRadius);
    }

    nodeGroup
        .append("a")
            .attr("class", "node-link")
            .attr("xlink:href", function(d) {
                return d.link;
            })
            .attr("target", "_blank")
        .append("svg:text")
        .style("opacity", 0)
        .attr("text-anchor", function(d) {
            return "start";
            // return d.children ? "end" : "start";
        })
        .attr("dx", function(d) {
            var gap = options.nodeRadius + 3;
            if (!d.children) {
                return gap;
            } else {
                return 5;
            }
        })
        .attr("dy", function(d) {
            if (!d.children) { 
                return 8;
            }
            else {
                return 15;
            }
        })
        .text(function(d) {
            return d.name;
        })
        .on("mouseover", function(d) {
            d3.select(this)
                .style("opacity", 1);
        })
        .on("mouseout", function(d) {
            d3.select(this)
                .style("opacity", .85);
        })
        .transition()
            .delay(600)
            .duration(600)
            .style("opacity", .85);

}

drawTree(data);

